        {
                new TransferOutput()
                {
                     AssetId = NativeContract.GAS.Hash,
                     ScriptHash = acc.ScriptHash,
                     Value = new BigDecimal(BigInteger.One,8)
                }
        }, acc.ScriptHash);

        Assert.IsNotNull(tx);

